$(document).ready(function () {
    const editBtn = $('.editBtn');
    const deleteBtn = $('.deleteBtn');
    var idGenre;

    $.ajaxSetup({
        headers: {
            'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
        }
    });

    $('#formTambahGenre').submit(function (e) { 
        e.preventDefault();
        var dataGenre = new FormData($(this)[0])
        $.ajax({
            type: "POST",
            url: "/admin/genre",
            data: dataGenre,
            contentType: false,
            processData: false,
            success: function (response) {
                $('#modalTambahGenre').modal('hide');
                Swal.fire({
                    icon: 'success',
                    title: response.message,
                    text: 'Reloading the Page',
                    timer: 800,
                    timerProgressBar: true,
                    didOpen: function() {
                        Swal.showLoading();
                    },
                    willClose: function() {
                        location.reload();
                    }
                });
            },
            error: function (xhr, status, error) {
                Swal.fire({
                    icon: 'error',
                    title: 'Error!',
                    text: 'Terjadi kesalahan!'
                });
            }
        });
    });

    editBtn.on("click", function () {
        idGenre = $(this).data("id");

        $.ajax({
            url: 'genre/' + idGenre,
            type: 'GET',
            cache: false,
            success: function(response) {
                $("#idGenre").val(response.data.id_genre);
                $("#editNamaGenre").val(response.data.nama_genre);
            }
        });
    });

    $("#formEditGenre").submit(function (e) { 
        e.preventDefault();
        var idGenre = $("#idGenre").val();
        var dataGenre = new FormData($(this)[0]);

        $.ajax({
            type: "POST",
            url: "/admin/genre/" + idGenre,
            data: dataGenre,
            contentType: false,
            processData: false,
            headers: {
                "X-HTTP-Method-Override": "PUT"
            },
            success: function (response) {
                $("#modalEditGenre").modal('hide');
                Swal.fire({
                    icon: 'success',
                    title: response.message,
                    text: 'Reloading the Page',
                    timer: 2100,
                    timerProgressBar: true,
                    didOpen: function() {
                        Swal.showLoading();
                    },
                    willClose: function() {
                        location.reload();
                    }
                });
            },
            error: function (xhr, status, error) {
                Swal.fire({
                    icon: 'error',
                    title: 'Error!',
                    text: 'Terjadi kesalahan!'
                });
            }
        });
    });

    deleteBtn.on('click', function(){
        idGenre = $(this).data("id");

        Swal.fire({
            icon: 'warning',
            title: 'Apakah anda yakin mau menghapus data ini?',
            showCancelButton: true,
            confirmButtonText: 'Ya',
            cancelButtonText: 'Tidak',
            }).then((result) => {
                if (result.isConfirmed) {
                    $.ajax({
                        type: "DELETE",
                        url: "/admin/genre/" + idGenre,
                        dataType: "json",
                        success: function (response) {
                            if (response.success) {
                                Swal.fire({
                                    icon: 'success',
                                    title: 'Data Buku Berhasil Dihapus!',
                                    text: 'Reloading the Page',
                                    timer: 2100,
                                    timerProgressBar: true,
                                    didOpen: function() {
                                        Swal.showLoading();
                                    },
                                    willClose: function() {
                                        location.reload();
                                    }
                                });
                            } else {
                                Swal.fire({
                                    icon: 'error',
                                    title: 'Error!',
                                    text: 'Data Buku Tidak Ditemukan!',
                                    timer: 2100,
                                    timerProgressBar: true,
                                });
                            }
                        }
                    });
                }
        });
    });
});