$(document).ready(function () {
    const editBtn = $('.editBtn');
    const deleteBtn = $('.deleteBtn');
    var idManga;

    $.ajaxSetup({
        headers: {
            'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
        }
    });

    $("#inMangaka, #inPenerbit, #inGenre").select2({
        theme: "bootstrap-5",
        width: $( this ).data( 'width' ) ? $( this ).data( 'width' ) : $( this ).hasClass( 'w-100' ) ? '100%' : 'style',
        dropdownParent: $('#modalTambahManga'),
        closeOnSelect: false,
        sorter: function(data) {
            return data.sort(function(a, b) {
              return a.text.localeCompare(b.text);
            });
        },
    });

    $("#editMangaka, #editPenerbit, #editGenre").select2({
        theme: "bootstrap-5",
        width: $( this ).data( 'width' ) ? $( this ).data( 'width' ) : $( this ).hasClass( 'w-100' ) ? '100%' : 'style',
        dropdownParent: $('#modalEditManga'),
        closeOnSelect: false,  
        sorter: function(data) {
            return data.sort(function(a, b) {
              return a.text.localeCompare(b.text);
            });
        },  
    });

    $('#inTglTersedia, #editTglTersedia').flatpickr({
        enableTime: false,
        dateFormat: "Y-m-d",
        disableMobile: true,
    });
    
    $('#inSlugManga').on({
        keydown: function(e) {
          if (e.which === 32)
            return false;
        },
        change: function() {
          this.value = this.value.replace(/\s/g, "");
        }
      });

    $('#formTambahManga').submit(function (e) { 
        e.preventDefault();
        var dataManga = new FormData($(this)[0])
        $.ajax({
            type: "POST",
            url: "/admin/manga",
            data: dataManga,
            contentType: false,
            processData: false,
            success: function (response) {
                $('#modalTambahManga').modal('hide');
                Swal.fire({
                    icon: 'success',
                    title: response.message,
                    text: 'Reloading the Page',
                    timer: 800,
                    timerProgressBar: true,
                    didOpen: function() {
                        Swal.showLoading();
                    },
                    willClose: function() {
                        location.reload();
                    }
                });
            },
            error: function (xhr, status, error) {
                Swal.fire({
                    icon: 'error',
                    title: 'Error!',
                    text: 'Terjadi kesalahan!'
                });
            }
        });
    });

    editBtn.on("click", function () {
        idManga = $(this).data("id");

        $.ajax({
            url: "/admin/manga/" + idManga,
            type: 'GET',
            cache: false,
            success: function(response) {
                $('#idManga').val(response.data.id_manga);
                $('#editJudulManga').val(response.data.judul_manga);
                $('#editJudulMangaJapanese').val(response.data.judul_jepang);
                $('#editMangaka').val(response.data.id_mangaka).trigger('change');
                $('#editPenerbit').val(response.data.id_penerbit).trigger('change');
                $('#editJumlahVolume').val(response.data.jml_vol);
                $('#editTglTersedia').val(response.data.tgl_tersedia);

                // Tampilkan data genreManga pada select2 multiple
                var genres = response.data.genre_manga;
                var genreIds = $.map(genres, function(genre) {
                    return genre.id_genre;
                });
                $('#editGenre').val(genreIds).trigger('change');                
            }
        });
    });

    $("#formEditManga").submit(function (e) { 
        e.preventDefault();
        var idManga = $("#idManga").val();
        var dataManga = new FormData($(this)[0]);

        $.ajax({
            type: "POST",
            url: "/admin/manga/" + idManga,
            data: dataManga,
            contentType: false,
            processData: false,
            headers: {
                "X-HTTP-Method-Override": "PUT"
            },
            success: function (response) {
                $("#modalEditManga").modal('hide');
                Swal.fire({
                    icon: 'success',
                    title: response.message,
                    text: 'Reloading the Page',
                    timer: 2100,
                    timerProgressBar: true,
                    didOpen: function() {
                        Swal.showLoading();
                    },
                    willClose: function() {
                        location.reload();
                    }
                });
            },
            error: function (xhr, status, error) {
                Swal.fire({
                    icon: 'error',
                    title: 'Error!',
                    text: 'Terjadi kesalahan!'
                });
            }
        });
    });

    deleteBtn.on('click', function(){
        idManga = $(this).data("id");

        Swal.fire({
            icon: 'warning',
            title: 'Apakah anda yakin mau menghapus data ini?',
            showCancelButton: true,
            confirmButtonText: 'Ya',
            cancelButtonText: 'Tidak',
            }).then((result) => {
                if (result.isConfirmed) {
                    $.ajax({
                        type: "DELETE",
                        url: "/admin/manga/" + idManga,
                        dataType: "json",
                        success: function (response) {
                            if (response.success) {
                                Swal.fire({
                                    icon: 'success',
                                    title: 'Data Manga Berhasil Dihapus!',
                                    text: 'Reloading the Page',
                                    timer: 2100,
                                    timerProgressBar: true,
                                    didOpen: function() {
                                        Swal.showLoading();
                                    },
                                    willClose: function() {
                                        location.reload();
                                    }
                                });
                            } else {
                                Swal.fire({
                                    icon: 'error',
                                    title: 'Error!',
                                    text: 'Data Manga Tidak Ditemukan!',
                                    timer: 2100,
                                    timerProgressBar: true,
                                });
                            }
                        }
                    });
                }
        });
    });
});