$(document).ready(function () {
    const editBtn = $('.editBtn');
    const deleteBtn = $('.deleteBtn');
    var idUser;

    $.ajaxSetup({
        headers: {
            'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
        }
    });

    $('#inTglLahir, #editTglLahir').flatpickr({
        enableTime: false,
        dateFormat: "Y-m-d",
        disableMobile: true,
    });

    $('#formTambahUser').submit(function (e) { 
        e.preventDefault();
        var dataUser = new FormData($(this)[0])
        $.ajax({
            type: "POST",
            url: "/admin/user",
            data: dataUser,
            contentType: false,
            processData: false,
            success: function (response) {
                $('#modalTambahUser').modal('hide');
                Swal.fire({
                    icon: 'success',
                    title: response.message,
                    text: 'Reloading the Page',
                    timer: 800,
                    timerProgressBar: true,
                    didOpen: function() {
                        Swal.showLoading();
                    },
                    willClose: function() {
                        location.reload();
                    }
                });
            },
            error: function (xhr) {
                var errors = xhr.responseJSON.errors;
                Swal.fire({
                    icon: 'error',
                    title: 'Error!',
                    text: 'Terjadi kesalahan!'
                });
                $.each(errors, function (key, value) { 
                    var errorHtml = '<span class="invalid-feedback" role="alert"><strong>' + value[0] + '</strong></span>';
                    $('#' + key).addClass('is-invalid').parent().append(errorHtml);
                });
            }
        });
    });

    editBtn.on("click", function () {
        idUser = $(this).data("id");

        $.ajax({
            url: 'user/' + idUser,
            type: 'GET',
            cache: false,
            success: function(response) {
                $("#idUser").val(response.data.id_user);
                $("#editNamaUser").val(response.data.nama_user);
                $("#editEmail").val(response.data.email);
                $("#editUsername").val(response.data.username);
                $("#editPassword").val(response.data.password);
                $("#editPassword").attr('placeholder', 'Password Tersembunyi(Hidden)!');
                $("#editNotelp").val(response.data.notelp);
                $("#editTglLahir").val(response.data.tgl_lahir);
                $("#previewFotoPreview").click(function (e) { 
                    e.preventDefault();
                    if (response.data.foto_profile !== null) {
                        Swal.fire({
                            imageUrl: "/images/profile/" + response.data.foto_profile,
                            imageHeight: 400,
                            imageAlt: 'Foto Profile'
                        });
                    } else {
                        Swal.fire({
                            icon: 'info',
                            title: 'Oops...',
                            text: 'Foto profile tidak ditemukan',                            
                        })
                    }                                         
                });
                $("#editIsAdmin").val(response.data.isAdmin);
            }
        });
    });

    $("#formEditUser").submit(function (e) { 
        e.preventDefault();
        var idUser = $("#idUser").val();
        var dataUser = new FormData($(this)[0]);

        $.ajax({
            type: "POST",
            url: "/admin/user/" + idUser,
            data: dataUser,
            contentType: false,
            processData: false,
            headers: {
                "X-HTTP-Method-Override": "PUT"
            },
            success: function (response) {
                $("#modalEditUser").modal('hide');
                Swal.fire({
                    icon: 'success',
                    title: response.message,
                    text: 'Reloading the Page',
                    timer: 2100,
                    timerProgressBar: true,
                    didOpen: function() {
                        Swal.showLoading();
                    },
                    willClose: function() {
                        location.reload();
                    }
                });
            },
            error: function (xhr, status, error) {
                Swal.fire({
                    icon: 'error',
                    title: 'Error!',
                    text: 'Terjadi kesalahan!'
                });
            }
        });
    });

    deleteBtn.on('click', function(){
        idUser = $(this).data("id");

        Swal.fire({
            icon: 'warning',
            title: 'Apakah anda yakin mau menghapus data ini?',
            showCancelButton: true,
            confirmButtonText: 'Ya',
            cancelButtonText: 'Tidak',
            }).then((result) => {
                if (result.isConfirmed) {
                    $.ajax({
                        type: "DELETE",
                        url: "/admin/user/" + idUser,
                        dataType: "json",
                        success: function (response) {
                            if (response.success) {
                                Swal.fire({
                                    icon: 'success',
                                    title: 'Data Buku Berhasil Dihapus!',
                                    text: 'Reloading the Page',
                                    timer: 2100,
                                    timerProgressBar: true,
                                    didOpen: function() {
                                        Swal.showLoading();
                                    },
                                    willClose: function() {
                                        location.reload();
                                    }
                                });
                            } else {
                                Swal.fire({
                                    icon: 'error',
                                    title: 'Error!',
                                    text: 'Data Buku Tidak Ditemukan!',
                                    timer: 2100,
                                    timerProgressBar: true,
                                });
                            }
                        }
                    });
                }
        });
    });
});