$(document).ready(function () {
    const editBtn = $('.editBtn');
    const deleteBtn = $('.deleteBtn');
    var idVolManga;

    $.ajaxSetup({
        headers: {
            'X-CSRF-TOKEN': $('meta[name="csrf-token"]').attr('content')
        }
    });

    $('.viewVisualArt').click(function () {
        visualArt = $(this).data("vart");
        judul = $(this).data("judul");
        Swal.fire({
            imageUrl: "/images/visualArt/" + judul + "/" + visualArt,
            imageHeight: 400,
            imageAlt: 'Visual Art'
        });        
    });

    $('#formTambahVolManga').submit(function (e) { 
        e.preventDefault();
        idVolManga = $('#addBtn').data("id");
        var dataVolManga = new FormData($(this)[0])
        $.ajax({
            type: "POST",
            url: "/admin/vol/" + idVolManga,
            data: dataVolManga,
            contentType: false,
            processData: false,
            success: function (response) {
                $('#modalTambahVolManga').modal('hide');
                Swal.fire({
                    icon: 'success',
                    title: response.message,
                    text: 'Reloading the Page',
                    timer: 800,
                    timerProgressBar: true,
                    didOpen: function() {
                        Swal.showLoading();
                    },
                    willClose: function() {
                        location.reload();
                    }
                });
            },
            error: function (xhr, status, error) {
                Swal.fire({
                    icon: 'error',
                    title: 'Error!',
                    text: 'Terjadi kesalahan!'
                });
            }
        });
    });

    editBtn.on("click", function () {
        idVolManga = $(this).data("id");

        $.ajax({
            url: '/admin/vol/' + idVolManga,
            type: 'GET',
            cache: false,
            success: function(response) {
                $("#idVolManga").val(response.data.id_vol);
                $("#idManga").val(response.data.id_manga);
                $("#editVolKe").val(response.data.vol_ke);
                $("#editDeskripsi").val(response.data.deskripsi);
                $("#editBahasa").val(response.data.bahasa);
                $("#editJmlHal").val(response.data.jml_hal);
                $("#editHarga").val(response.data.harga);
                $("#previewVisualArt").click(function (e) { 
                    e.preventDefault();
                    if (response.data.foto_profile !== null) {
                        Swal.fire({
                            imageUrl: "/images/visualArt/" + response.data.manga.judul_manga + "/" + response.data.visual_art,
                            imageHeight: 400,
                            imageAlt: 'Visual Art'
                        });
                    } else {
                        Swal.fire({
                            icon: 'info',
                            title: 'Oops...',
                            text: 'Visual Art tidak ditemukan',                            
                        })
                    }                                         
                });
            }
        });
    });

    $("#formEditVolManga").submit(function (e) { 
        e.preventDefault();
        var idVolManga = $("#idVolManga").val();
        var dataVolManga = new FormData($(this)[0]);

        $.ajax({
            type: "POST",
            url: "/admin/vol/" + idVolManga,
            data: dataVolManga,
            contentType: false,
            processData: false,
            headers: {
                "X-HTTP-Method-Override": "PUT"
            },
            success: function (response) {
                $("#modalEditVolManga").modal('hide');
                Swal.fire({
                    icon: 'success',
                    title: response.message,
                    text: 'Reloading the Page',
                    timer: 2100,
                    timerProgressBar: true,
                    didOpen: function() {
                        Swal.showLoading();
                    },
                    willClose: function() {
                        location.reload();
                    }
                });
            },
            error: function (xhr, status, error) {
                Swal.fire({
                    icon: 'error',
                    title: 'Error!',
                    text: 'Terjadi kesalahan!'
                });
            }
        });
    });

    deleteBtn.on('click', function(){
        idVolManga = $(this).data("id");

        Swal.fire({
            icon: 'warning',
            title: 'Apakah anda yakin mau menghapus data ini?',
            showCancelButton: true,
            confirmButtonText: 'Ya',
            cancelButtonText: 'Tidak',
            }).then((result) => {
                if (result.isConfirmed) {
                    $.ajax({
                        type: "DELETE",
                        url: "/admin/vol/" + idVolManga,
                        dataType: "json",
                        success: function (response) {
                            if (response.success) {
                                Swal.fire({
                                    icon: 'success',
                                    title: 'Data Buku Berhasil Dihapus!',
                                    text: 'Reloading the Page',
                                    timer: 2100,
                                    timerProgressBar: true,
                                    didOpen: function() {
                                        Swal.showLoading();
                                    },
                                    willClose: function() {
                                        location.reload();
                                    }
                                });
                            } else {
                                Swal.fire({
                                    icon: 'error',
                                    title: 'Error!',
                                    text: 'Data Buku Tidak Ditemukan!',
                                    timer: 2100,
                                    timerProgressBar: true,
                                });
                            }
                        }
                    });
                }
        });
    });
});